function updateButton() {
  const icon = this.paused
    ? ` <i class="fas fa-play"></i>`
    : `<i class="fas fa-pause"></i>`;
  toggle.innerHTML = icon;

  const iconVideo = this.paused ? ` <i class="fas fa-play"></i>` : ``;
  iconBtn.innerHTML = iconVideo;
}

